mapper.select:
select u.fuid as fuid , u.funm as funm , u.futype as futype , (select fkind3 from tcommcd where fkind2='COMBO_Futype' and fvalue = u.futype) as futypenm , u.fregdt as fregdt , u.fmoddt as fmoddt , u.fauthtype as fauthtype , (select fkind3 from tcommcd where fkind2='COMBO_FAuthType' and fvalue = u.fauthtype) as fauthtypenm , u.fpin as fpin , u.fauthyn as fauthyn , u.fsidx as fsidx , u.fgroupid as fgroupid , u.fpartnm1 as fpartnm1 , u.fpartnm2 as fpartnm2 , u.fpartnm3 as fpartnm3 , u.fpartcd1 as fpartcd1 , (select fvalue from tcommcd where fkind2='centercd' and fkind3 = u.fpartcd1) as fpartcdnm1 , u.fpartcd2 as fpartcd2 , u.fpartcd3 as fpartcd3 , u.fsstatus as fsstatus , u.fqstatus as fqstatus , DATE_FORMAT(u.fusdt,'%Y-%m-%d') as fusdt , DATE_FORMAT(u.fuedt,'%Y-%m-%d') as fuedt , u.fustatus as fustatus , u.ftel as ftel , u.fcarno as fcarno , u.fvisitnum as fvisitnum , e.fgateuid as efgateuid , c.fcdno as cfcdno , date_FORMAT(c.fsdt,'%Y-%m-%d') as cfsdt , date_FORMAT(c.fedt,'%Y-%m-%d') as cfedt , c.fcdnum as cfcdnum , c.ftype as cftype , c.fstatus as cfstatus , (select fkind3 from tcommcd where fkind2='COMBO_Fcardstatus' and fvalue = c.fstatus) as cfstatusnm , c.fsidx as cfsidx , c.fauthyn as cfauthyn , c.fregdt as cfregdt , c.fmoddt as cfmoddt , c.famodt as cfamodt , c.faupdt as cfaupdt , c.fqstatus as cfqstatus , c.fsstatus as cfsstatus
from tuserinfo_main u left outer join tcard_main c on (c.fstatus is null or length( c.fstatus) = 0 or c.fstatus = 'Y') and c.fuid = u.fuid left outer join tusertogate_main e on e.fuid = u.fuid and e.fcdno = c.fcdno where (u.fustatus is null or length( u.fustatus) = 0 or u.fustatus = 'Y') and u.fpartcd1 != '10'
and u.fpartcd1 = #{srchCond}
and u.funm like concat('%', #{srchCondWord},'%')
and DATE_FORMAT(u.fusdt,'%Y-%m-%d') >= #{srchFusdt}
and DATE_FORMAT(u.fuedt,'%Y-%m-%d') <= #{srchFuedt}
and DATE_FORMAT(c.fsdt,'%Y-%m-%d') >= #{srchCfsdt}
and DATE_FORMAT(c.fedt,'%Y-%m-%d') <= #{srchCfedt}
and c.fcdnum like concat('%', #{srchCardNum},'%')
and u.fvisitnum like concat('%', #{srchTVCardNum},'%') and u.futype = '9'
and u.fvisitnum like concat('%', #{srchTVCardNum},'%') and u.futype in ('5','6','7','8')
and u.fvisitnum like concat('%', #{srchTVCardNum},'%') and u.futype in ('5','6','7','8','9')
and IFNULL(u.fvisitnum,'') like concat('%', #{srchTVCardNum},'%')
and u.fpartnm1 like concat('%', #{srchPartnmWord},'%')
and u.fpartnm2 like concat('%', #{srchPartnmWord},'%')
and u.fpartnm3 like concat('%', #{srchPartnmWord},'%')
and e.fgateuid is not null
order by u.funm asc, u.fuid asc limit #{srchCnt} offset #{offset}

mapper.delete:
 	delete from tuserauthgroup_${fpartcd1}
	where	fuid = #{fuid} 
	and		fcdno = #{cfcdno}

mapper.update:
update tauthtogate_main_${fpartcd1}
set		fuseyn = 'N'
,		fsstatus = 'Q' 
where	fuid = #{fuid} 
and		fcdno = #{cfcdno}

update	tlocationtree
set		ftype = #{ftype}
where	ftid = #{ftid}

update	tuserauthgroup_${centerCode} 
set 	fsstatus='Q' 
where	ftid = #{fptid}

update	tuserauthgroup_${fpartcd1} 
set		fsstatus = 'Q' 
where	fuid = #{fuid} 
and		fcdno = #{fcdno}

mapper.select:
SELECT t.fuid , c.fcdno , t.funm , t.fpartnm1 , t.fpartnm2 , t.fpartnm3 FROM TUSERINFO_MAIN t inner join tcard_main c on t.fuid=c.fuid WHERE t.fuid IN ( select ua.fuid from tuserauthgroup_${centerCode} ua inner join tlocationtree lt on ua.ftid = lt.ftid where lt.ftid in
#{auth}
and ua.fuseyn = 'Y' )
and t.funm like concat('%', #{funm},'%')
group by t.fuid limit #{srchCnt} offset #{offset}

select ${chkValueArray} from ( SELECT t.fuid as fuid , t.funm as funm , t.fpartnm1 as fpartnm1 , t.fpartnm2 as fpartnm2 , t.fpartnm3 as fpartnm3 FROM TUSERINFO_MAIN t inner join tcard_main c on t.fuid=c.fuid WHERE t.fuid IN ( select ua.fuid from tuserauthgroup_${centerCode} ua inner join tlocationtree lt on ua.ftid=lt.ftid where lt.ftid in
#{auth}
and ua.fuseyn='Y'
and t.funm like concat('%', #{unm},'%')
) group by t.fuid ) userList
