mapper.insert:
insert	into	tcommcd
				(
					fkind1, fkind2, fkind3, fuseyn, fvalue
				)
	  values 	(
	  				'code', 'centercd', (select IFNULL(MAX(fkind3)+1, 10) from tcommcd as tt where fkind1 = 'code' and fkind2 = 'centercd'),'Y', #{fvalue}
				)

mapper.update:
update tcommcd
   set useyn = #{fuseyn}
 where fkind1 = 'code'
   and fkind2 = 'centercd'
   and fkind3 = #{fkind3}

mapper.select:
select tsu.fsiteid, tsu.fpasswd, tsu.fpasswdyn, tsu.fkind3, tsu.fname, tsu.fphone, tsu.fuseyn, tsu.fregdt , DATE_FORMAT(fregdt,'%Y-%m-%d') AS fregdt2 , (select fvalue from tcommcd where fkind1='code' and fkind2='centercd' and fkind3 = tsu.fkind3) as fvalue , tsu.femergency , (select fkind3 from tcommcd where fkind1='combo' and fkind2='COMBO_FEmergency' and fvalue = tsu.femergency) as femergencynm , tsu.fauthcd , (select max(fvalue) from tcommcd where fkind1='code' and fkind2='fauthcd' and fkind3 = tsu.fauthcd) as fauthnm , tsu.fuid , (select max(t.funm) from tuserinfo_main t where t.fuid = tsu.fuid) as funm , tsu.site_id , tsu.author_id , (select au.author_nm from author_tb au where au.author_id = tsu.author_id) as author_nm , (select st.site_nm from site_tb st where st.site_id = tsu.site_id) as site_nm from tsiteuser tsu where 1=1
and site_id = #{site_id}
and fsiteid like concat('%', #{fsiteid}, '%')
and fname like concat('%', #{fname}, '%')
and author_id = #{author_id}
order by tsu.site_id asc limit #{srchCnt} offset #{offset}

select count(1) from tsiteuser tsu where 1=1
and site_id = #{site_id}
and fsiteid like concat('%', #{fsiteid}, '%')
and fname like concat('%', #{fname}, '%')
and author_id = #{author_id}

select	COUNT(1) as usedCnt
  from	tsiteuser
 where  fsiteid = #{fsiteid}

mapper.insert:
insert	into	tsiteuser
				(
					fsiteid, fpasswd, fpasswdyn, fname, fphone, fuseyn, fpasswdmodt, fuid, site_id, author_id
				)
	  values 	(
	  				#{fsiteid}, #{fpasswd}, 'N', #{fname}, #{fphone}, 'Y', now(), #{fuid}, #{site_id}, #{author_id}
				)

mapper.update:
update  tsiteuser
   set 	fname = #{fname}
   	,	fphone = #{fphone}
   	, 	fuid = #{fuid}
   	, 	site_id = #{site_id}
   	,	author_id = #{author_id}
 where	fsiteid = #{fsiteid}

update  tsiteuser
   set 	fuseyn = #{fuseyn}
 where	fsiteid = #{fsiteid}

update  tsiteuser
   set 	fpasswd = #{fpasswd}
	,	fpasswdyn = 'N'
	,	fpasswdmodt = now()
 where	fsiteid = #{fsiteid}

mapper.select:
select	COUNT(1) as cnt
  from	tsiteuser
 where  fsiteid = #{fsiteid}
   and	fpasswd = #{fpasswd}

mapper.update:
update  tsiteuser
   set 	fpasswd = #{fpasswd}
	,	fpasswdyn = 'Y'
	,	fpasswdmodt = now()
 where	fsiteid = #{fsiteid}

mapper.select:
	select	fkind1, fkind2, fkind3, fuseyn, fvalue
      from	tcommcd
     where	1=1
       and	fkind1 = #{fkind1}
       and	fkind2 = #{fkind2}
       and	fuseyn = 'Y'
       and	left(fkind3,2) = #{fkind3}
     order by forder, fkind3 asc

	select	fkind1, fkind2, fkind3, fuseyn, fvalue
      from	tcommcd
     where	1=1
       and	fkind1 = #{fkind1}
       and	fkind2 = #{fkind2}
       and	fuseyn = 'Y'
       and	left(fkind3,4) = #{fkind3}
     order by forder, fkind3 asc

    	select count(a.fsiteid) as cnt
    	from tsiteuser a
    	where a.fuid = #{fuid}

			and a.fsiteid != #{fsiteid}

select count(*) 
	from device_info_tb
where device_id = #{checkId}

select max(idx)+1
  from floor_management_tb
 where site_id = #{siteId}

