mapper.select:
select count(fmt.floor) as cnt
	from floor_management_tb fmt 
where fmt.floor = #{newFloor}
  and fmt.site_id = #{siteId}
  and fmt.use_yn = "Y"

select count(device_floor) as deviceFloor
	from device_info_tb dit
where dit.site_id = #{siteId}
  and dit.device_floor = #{floor}

select count(floor) as deviceFloor
	from floor_management_tb fmt
where fmt.site_id = #{siteId}
  and fmt.floor = #{newFloor}
  and use_yn = "Y"

select fevttm  /*[BasicInfo.selectSysLogInfo]*/
     , fsiteid 
     , fsyscode
     , fdetail     
     , fcnntip 
  from tsyslog 
 where fevttm = #{fevttm}
   and fsiteid = #{fsiteid}
   and fsyscode = #{fsyscode}

select sum(cf_dup) from (select count(1) as cf_dup from gatelog_set_tb where use_yn = 'Y' and ( str_to_date(#{keep_sdt},'%Y-%m-%d') between keep_sdt and keep_edt or str_to_date(#{keep_edt},'%Y-%m-%d') between keep_sdt and keep_edt )
and keep_seq <> #{keep_seq}
union all select sum(case when keep_sdt between str_to_date(#{keep_sdt},'%Y-%m-%d') and str_to_date(#{keep_edt},'%Y-%m-%d') or keep_edt between str_to_date(#{keep_sdt},'%Y-%m-%d') and str_to_date(#{keep_edt},'%Y-%m-%d') then 1 else 0 end) as cf_dup from gatelog_set_tb
where keep_seq <> #{keep_seq}
) t

mapper.update:
update gatelog_set_tb
   set keep_sdt = str_to_date(#{keep_sdt},'%Y-%m-%d')
     , keep_edt = str_to_date(#{keep_edt},'%Y-%m-%d')
     , keep_unit = #{keep_unit}
     , keep_num = #{keep_num}
     , use_yn = #{use_yn}
     , mod_dt = now(3)
     , mod_id = #{mod_id}
 where keep_seq = #{keep_seq}
