mapper.select:
SELECT NVL(MAX(file_sn) + 1, 1)
FROM file_detail_tb
WHERE atch_file_id = #{atchFileId}

mapper.insert:
INSERT INTO file_tb(
	atch_file_id
	, creat_dt
	, use_at
) VALUES(
	#{atchFileId}
	, now()
	, 'Y'
)

INSERT INTO file_detail_tb(
	atch_file_id
	, file_sn
	, file_stre_cours
	, stre_file_nm
	, orignl_file_nm
	, file_extsn
	, file_cn
	, file_size
) VALUES(
	#{atchFileId}
	, #{fileSn}
	, #{fileStreCours}
	, #{streFileNm}
	, #{orignlFileNm}
	, #{fileExtsn}
	, #{fileCn}
	, #{fileSize}
)

mapper.update:
UPDATE file_detail_tb 

	USE_YN = #{useAt}


	atch_file_id = #{atchFileId}

		AND file_sn = #{fileSn}

UPDATE file_tb 
SET use_at = #{useAt}
WHERE atch_file_id = #{atchFileId}

mapper.select:
SELECT ATCH_FILE_ID as atchFileId
  FROM FLOOR_MANAGEMENT_TB
 WHERE SITE_ID = #{siteId} AND idx = #{idx}
