mapper.select:
SELECT m.fgid as gid, m.fip as ip, m.flname as name, s.facnt as acnt, s.fucnt as ucnt, TIMESTAMPDIFF( SECOND, s.fmoddt , now()) as aliveDiff, s.fterminalst as terminalStatus, s.fdoorst as doorStatus, s.fcoverst as coverStatus, s.flockst as lockStatus, s.fopenst as openStatus, m.fversion as VERSION FROM tgatemst m INNER JOIN tgatemst_sidx s ON m.fgid = s.fgid
INNER JOIN tlocationtree t ON m.fgid = t.fgid
AND t.fptid in ( SELECT ftid FROM tlocationtree WHERE fptid = #{searchItemArea} )
AND t.fptid = #{searchItemFloor}
WHERE 1=1 AND m.fgid LIKE concat(#{searchItemCenter},'%') order by m.fgid ASC;

