mapper.select:
select u.fuid as fuid, u.funm as funm, u.futype as futype, u.fauthtype as fauthtype, u.fpartnm1 as fpartnm1, u.fpartnm2 as fpartnm2, u.fpartnm3 as fpartnm3, (SELECT flname FROM tgatemst WHERE fgid=a.fgid) as fgname, a.fcdno as cfcdno from tuserinfo_main u inner join tauthtogate_main_11 a on u.fuid = a.fuid where 1=1 and a.fgid in
#{gate}
and a.fuseyn = 'Y'
and u.funm like concat(#{unm},'%')
limit #{srchCnt} offset #{offset}

select u.fuid as fuid, u.funm as funm, u.futype as futype, u.fauthtype as fauthtype, u.fpartnm1 as fpartnm1, u.fpartnm2 as fpartnm2, u.fpartnm3 as fpartnm3, (SELECT flname FROM tgatemst WHERE fgid=a.fgid) as fgname, a.fcdno as cfcdno from tuserinfo_main u inner join tauthtogate_main_12 a on u.fuid = a.fuid where 1=1 and a.fgid in
#{gate}
and a.fuseyn = 'Y'
and u.funm like concat(#{unm},'%')
limit #{srchCnt} offset #{offset}

select ${chkValueArray} from ( select u.fuid as fuid, u.funm as funm, u.futype as futype, u.fauthtype as fauthtype, u.fpartnm1 as fpartnm1, u.fpartnm2 as fpartnm2, u.fpartnm3 as fpartnm3, (SELECT flname FROM tgatemst WHERE fgid=a.fgid) as fgname, a.fcdno as cfcdno from tuserinfo_main u inner join tauthtogate_main_11 a on u.fuid = a.fuid where 1=1 and a.fgid in
#{gate}
and a.fuseyn = 'Y'
and u.funm like concat(#{unm},'%')
) userList

