mapper.select:
select count(*) duplcnt
from tgateshtype
where fnm = #{fnm}

select count(a.fsidx) as cnt from ( select '11' as cond, a.fsidx from ttcmd_main_11 a inner join tgatemst b on a.fgid = b.fgid where a.ftx in ('CMD_SEND_MESSAGE','CMD_SET_TERMINAL_OPTION') and a.frecvdt >= #{startDateTime} and a.frecvdt <= concat(#{endDateTime},':59')
and exists (select * from tlocationtree t where a.fgid = t.fgid and t.fptid like concat(#{ftid}, '%'))
and a.ftx = #{srchFtx}
and b.flname like concat('%', #{srchFlnam}, '%')
and a.fsstatus in ('E','F')
and a.fsstatus = #{srchFstat}
union all SELECT '12' as cond, a.fsidx FROM ttcmd_main_12 a inner join tgatemst b on a.fgid = b.fgid where a.ftx in ('CMD_SEND_MESSAGE','CMD_SET_TERMINAL_OPTION') and a.frecvdt >= #{startDateTime} and a.frecvdt <= concat(#{endDateTime},':59')
and exists (select * from tlocationtree t where a.fgid = t.fgid and t.fptid like concat(#{ftid}, '%'))
and a.ftx = #{srchFtx}
and b.flname like concat('%', #{srchFlnam}, '%')
and a.fsstatus in ('E','F')
and a.fsstatus = #{srchFstat}
) a
where a.cond = #{srchCond}

select a.* from ( select '11' as cond, a.fsidx, a.fgid, b.flname as flnam, b.fpartcd1 as fpartcd1, b.fip, a.frecvdt, a.fprocid, a.ftx, a.fex, a.fvalue, a.fprodt, a.fregdt, a.fstadt, a.fmoddt, a.fackdt, a.fenddt, a.fprocd, a.fprodoc, a.fqstatus, a.fsstatus, (case when a.fsstatus = 'Q' then '대기' when a.fsstatus = 'U' then '완료' when a.fsstatus in ('E','F') then '실패' else a.fsstatus end ) as fsstatusnm, (case when a.ftx = 'CMD_TERMINAL_KOPEN' and a.fvalue = '1' then 'KeepOpen' when a.ftx = 'CMD_TERMINAL_KOPEN' and a.fvalue = '0' then 'Open' when a.ftx = 'CMD_TERMINAL_KOPEN' and a.fvalue = '2' then 'Close' when a.ftx = 'CMD_TERMINAL_LOCK' and a.fvalue = '0' then 'UnLock' when a.ftx = 'CMD_TERMINAL_LOCK' and a.fvalue = '1' then 'Lock' when a.ftx = 'CMD_DELETE_ALL_USER' and a.fvalue = '0' then 'Reset User' when a.ftx = 'CMD_SET_TERMINAL_OPTION' then 'Schedule' when a.ftx = 'CMD_SEND_MESSAGE' then 'Message' end ) as ftxnm from ttcmd_main_11 a inner join tgatemst b on a.fgid = b.fgid where a.ftx in ('CMD_SEND_MESSAGE','CMD_SET_TERMINAL_OPTION','CMD_DELETE_ALL_USER','CMD_TERMINAL_KOPEN','CMD_TERMINAL_LOCK') and a.frecvdt >= #{startDateTime} and a.frecvdt <= concat(#{endDateTime},':59')
and exists (select * from tlocationtree t where a.fgid = t.fgid and t.fptid like concat(#{ftid}, '%'))
and a.ftx = #{srchFtx}
and b.flname like concat('%', #{srchFlnam}, '%')
and a.fsstatus in ('E','F')
and a.fsstatus = #{srchFstat}
union all select '12' as cond, a.fsidx, a.fgid, b.flname as flnam, b.fpartcd1 as fpartcd1, b.fip, a.frecvdt, a.fprocid, a.ftx, a.fex, a.fvalue, a.fprodt, a.fregdt, a.fstadt, a.fmoddt, a.fackdt, a.fenddt, a.fprocd, a.fprodoc, a.fqstatus, a.fsstatus, (case when a.fsstatus = 'Q' then '대기' when a.fsstatus = 'U' then '완료' when a.fsstatus in ('E','F') then '실패' else a.fsstatus end ) as fsstatusnm, (case when a.ftx = 'CMD_TERMINAL_KOPEN' and a.fvalue = '1' then 'KeepOpen' when a.ftx = 'CMD_TERMINAL_KOPEN' and a.fvalue = '0' then 'Open' when a.ftx = 'CMD_TERMINAL_KOPEN' and a.fvalue = '2' then 'Close' when a.ftx = 'CMD_TERMINAL_LOCK' and a.fvalue = '0' then 'UnLock' when a.ftx = 'CMD_TERMINAL_LOCK' and a.fvalue = '1' then 'Lock' when a.ftx = 'CMD_DELETE_ALL_USER' and a.fvalue = '0' then 'Reset User' when a.ftx = 'CMD_SET_TERMINAL_OPTION' then 'Schedule' when a.ftx = 'CMD_SEND_MESSAGE' then 'Message' end ) as ftxnm from ttcmd_main_12 a inner join tgatemst b on a.fgid = b.fgid where a.ftx in ('CMD_SEND_MESSAGE','CMD_SET_TERMINAL_OPTION','CMD_DELETE_ALL_USER','CMD_TERMINAL_KOPEN','CMD_TERMINAL_LOCK') and a.frecvdt >= #{startDateTime} and a.frecvdt <= concat(#{endDateTime},':59')
and exists (select * from tlocationtree t where a.fgid = t.fgid and t.fptid like concat(#{ftid}, '%'))
and a.ftx = #{srchFtx}
and b.flname like concat('%', #{srchFlnam}, '%')
and a.fsstatus in ('E','F')
and a.fsstatus = #{srchFstat}
) a where 1=1
and a.cond = #{srchCond}
and a.ftxnm = #{srchFtxnm}
order by a.fsidx desc limit #{srchCnt} offset #{offset}

