select count(1) cnt
  from tgatemst
  where fvname = #{fvname}

mapper.update:
UPDATE TGATEMST SET 
	FLNAME = #{flname},
	FVNAME = #{fvname},
	FIP = #{fip}
WHERE FGID = #{fgid}

UPDATE TGATEMST SET
	FUSEYN = #{fuseyn}
WHERE FGID = #{fgid}

mapper.select:
select site_id as fpartcd1 , device_id as fgid , device_no as fvname , device_nm as flname , use_yn as fuseyn from device_info_tb where site_id = #{fpartcd1}
and device_nm like concat('%', #{flname}, '%')
order by device_id
